* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  overflow-x: hidden; /* Horizontal scroll remove */
}

/* Jo bhi element overflow karega, uski scrollbar hide ho jayegi */
::-webkit-scrollbar {
  display: none; /* Chrome, Safari */
}
body,
html {
  width: 100%;
  height: 100%;
}
body {
  overflow-y: scroll; /* Scroll allow karega */
  scrollbar-width: none; /* Firefox ke liye */
  -ms-overflow-style: none; /* IE aur Edge ke liye */
}

body::-webkit-scrollbar {
  display: none; /* Chrome, Safari ke liye */
}

.container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
  width: 100vw;
  height: 100vh;
  overflow-y: scroll;
  scrollbar-width: none; /* Firefox ke liye */
  -ms-overflow-style: none;
}
.container::-webkit-scrollbar {
  display: none;
}

/* CSS */
.button-64 {
  align-items: center;
  background-image: linear-gradient(144deg, #af40ff, #5b42f3 50%, #00ddeb);
  border: 0;
  border-radius: 8px;
  box-shadow: rgba(151, 65, 252, 0.2) 0 15px 30px -5px;
  box-sizing: border-box;
  color: #ffffff;
  display: flex;
  font-family: Phantomsans, sans-serif;
  font-size: 20px;
  justify-content: center;
  line-height: 1em;
  max-width: 100%;
  min-width: 140px;
  padding: 3px;
  text-decoration: none;
  user-select: none;
  -webkit-user-select: none;
  touch-action: manipulation;
  white-space: nowrap;
  cursor: pointer;
}

.button-64:active,
.button-64:hover {
  outline: 0;
}

.button-64 span {
  background-color: rgb(5, 6, 45);
  padding: 16px 24px;
  border-radius: 6px;
  width: 100%;
  height: 100%;
  transition: 300ms;
}

.button-64:hover span {
  background: none;
}

@media (min-width: 768px) {
  .button-64 {
    font-size: 24px;
    min-width: 196px;
  }
}

.button-92 {
  --c: #fff;

  background: linear-gradient(90deg, #0000 33%, #fff5, #0000 67%)
      var(--_p, 100%) / 300% no-repeat,
    #004dff;

  color: #0000;
  border: none;
  transform: perspective(500px) rotateY(calc(20deg * var(--_i, -1)));
  text-shadow: calc(var(--_i, -1) * 0.08em) -0.01em 0 var(--c),
    calc(var(--_i, -1) * -0.08em) 0.01em 2px #0004;
  outline-offset: 0.1em;
  transition: 0.3s;
}

.button-92:hover,
.button-92:focus-visible {
  --_p: 0%;
  --_i: 1;
}

.button-92:active {
  text-shadow: none;
  color: var(--c);
  box-shadow: inset 0 0 9e9Q #0005;
  transition: 0s;
}

.button-92 {
  max-width: 270px;
  min-width: 160px;
  font-weight: bold;
  font-size: -2rem;
  margin: 0;
  cursor: pointer;
  padding: 0.1em 0.3em;
}
.title {
  margin-left: 182px;
  font-family: "Poppins", sans-serif;
  font-weight: bold;
  margin-bottom: 75px;
}
.subtexts {
