}
/* Doctors Section */
.doctors-container {
  background-color: rgb(59 130 246);
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  padding: 20px;
}

.doctors-content {
  display: flex;
  flex-direction: row; /* Left-Right Layout */
  max-width: 1200px;
  width: 100%;
  background: white;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
}

.doctors-image {
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
}

.doctors-text {
  display: flex;
  flex-direction: column;
  flex: 1;
  padding: 40px;
  text-align: left;
  margin-top: 190px;
  margin-left: 54px;
}

.doctors-text .title {
  font-size: 32px;
  font-weight: bold;
  color: #333;
  margin-bottom: 20px;
}

.doctors-text .texts {
