}

/* Rewards Section - 3 Cards in 1 Row */
.rewards-container {
  text-align: center;
  padding: 40px;
}

.rewards-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* 3 cards per row */
  gap: 20px;
  justify-content: center;
  padding: 20px;
}

