import axios from "axios";

// Action Types
export const FETCH_APPOINTMENTS_REQUEST = "FETCH_APPOINTMENTS_REQUEST";
export const FETCH_APPOINTMENTS_SUCCESS = "FETCH_APPOINTMENTS_SUCCESS";
export const FETCH_APPOINTMENTS_FAIL = "FETCH_APPOINTMENTS_FAIL";

export const ADD_APPOINTMENT_REQUEST = "ADD_APPOINTMENT_REQUEST";
export const ADD_APPOINTMENT_SUCCESS = "ADD_APPOINTMENT_SUCCESS";
export const ADD_APPOINTMENT_FAIL = "ADD_APPOINTMENT_FAIL";

export const DELETE_APPOINTMENT_REQUEST = "DELETE_APPOINTMENT_REQUEST";
export const DELETE_APPOINTMENT_SUCCESS = "DELETE_APPOINTMENT_SUCCESS";
export const DELETE_APPOINTMENT_FAIL = "DELETE_APPOINTMENT_FAIL";

// ‚úÖ Fetch Appointments
export const fetchAppointments = () => async (dispatch) => {
  try {
    console.log("üî• Fetching Appointments..."); // ‚úÖ Debugging Step 1
    dispatch({ type: FETCH_APPOINTMENTS_REQUEST });

    const { data } = await axios.get("/api/appointments"); // üî• API Call
    console.log("‚úÖ Appointments Fetched:", data); // ‚úÖ Debugging Step 2

    dispatch({
      type: FETCH_APPOINTMENTS_SUCCESS,
      payload: data,
    });
  } catch (error) {
    console.error("‚ùå Fetch Appointments Failed:", error.response?.data?.message || error.message); // ‚úÖ Debugging Step 3
    dispatch({
      type: FETCH_APPOINTMENTS_FAIL,
      payload: error.response?.data?.message || error.message,
    });
  }
};

// ‚úÖ Add New Appointment
export const addAppointment = (appointmentData) => async (dispatch) => {
  try {
    console.log("üî• Adding Appointment...", appointmentData); // ‚úÖ Debugging Step 1
    dispatch({ type: ADD_APPOINTMENT_REQUEST });

    const { data } = await axios.post("/api/appointments", appointmentData); // üî• API Call
    console.log("‚úÖ Appointment Added:", data); // ‚úÖ Debugging Step 2

    dispatch({
      type: ADD_APPOINTMENT_SUCCESS,
      payload: data,
    });
  } catch (error) {
    console.error("‚ùå Add Appointment Failed:", error.response?.data?.message || error.message); // ‚úÖ Debugging Step 3
    dispatch({
      type: ADD_APPOINTMENT_FAIL,
      payload: error.response?.data?.message || error.message,
    });
  }
};

// ‚úÖ Delete Appointment
export const deleteAppointment = (id) => async (dispatch) => {
  try {
    console.log(`üî• Deleting Appointment ID: ${id}`); // ‚úÖ Debugging Step 1
    dispatch({ type: DELETE_APPOINTMENT_REQUEST });

    await axios.delete(`/api/appointments/${id}`); // üî• API Call
    console.log(`‚úÖ Appointment Deleted: ${id}`); // ‚úÖ Debugging Step 2

    dispatch({
      type: DELETE_APPOINTMENT_SUCCESS,
      payload: id,
    });
  } catch (error) {
    console.error("‚ùå Delete Appointment Failed:", error.response?.data?.message || error.message); // ‚úÖ Debugging Step 3
    dispatch({
      type: DELETE_APPOINTMENT_FAIL,
      payload: error.response?.data?.message || error.message,
    });
  }
};
